import React from 'react';
import { useState } from "react";

const ActivityList = ({ activity }) => {



    const [data, setData] = useState([
        { id: 1, name: "John", age: 25 },
        { id: 2, name: "Jane", age: 30 },
        { id: 3, name: "Bob", age: 35 },
    ]);

    const handleDelete = (id) => {
        const newData = data.filter((performed) => performed.id !== id);
        setData(newData);
    };


    return (
        <table className='table'>
            <thead>
                <tr>
                    <th scope="col">Activity</th>
                    <th scope="col">Date</th>
                    <th scope="col">Start Time</th>
                    <th scope="col">End Time</th>
                    <th scope="col">Duration</th>
                </tr>
            </thead>
            <tbody>
                {activity.map((performed) => (
                    <tr key={performed.id}>
                        <td>{performed.activityName}</td>
                        <td>{performed.activityDate}</td>
                        <td>{performed.startTime}</td>
                        <td>{performed.endTime}</td>
                        <td>{performed.activityDistance}</td>
                        <td>
                            <button onClick={() => handleDelete(performed.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>

    )
}







export default ActivityList;
