import './App.css';
import TrackerForm from './components/TrackerForm';
import ActivityList from './components/ActivityList';
import { useState } from "react";


const sampleActivity = [
  {
    id: 1,
    activityName: "Run"
  },
  {
    id: 2,
    activityName: "Swim"
  },
  {
    id: 3,
    activityName: "Hike"
  },
  {
    id: 4,
    activityName: "Walk"
  },
  {
    id: 5,
    activityName: "other"
  }

];




function App() {

  const [activityEntry, setActivityEntry] = useState(sampleActivity);

  const addActivityEntry = (newFormEntry) => {
    let randomId = Math.floor(Math.random() * 10) + 1

    newFormEntry.id = randomId;

    setActivityEntry([...activityEntry, newFormEntry]);
  };

  return (


    // <div id="App">
    //   <div options="select-container">
    //     <select>
    //       {sampleActivity.map((sampleActivity) => (
    //         <option value={sampleActivity.id}>{sampleActivity.activityName}</option>
    //       ))}
    //     </select>
    //   </div>
    // </div>
    <div className="container-sm p-2">
      <header className="p-3 text-center bg-dark text-light rounded-3">
        <h2>Activity Tracker</h2>
      </header>
      <div className="container">
        <div className='row justify-content-center'>
          <div className='col-4'>
            <ActivityList activity={activityEntry} />
          </div>
        </div>
      </div>
      <div className="container">
        <div className='row justify-content-center'>
          <div className='col-4'>
            <TrackerForm onSubmitHandler={addActivityEntry} />
          </div>
        </div>
      </div>
    </div>
  );
}


export default App;
