import React from "react";
// import './App.js';
import { useState } from 'react';



const ActivityList = ({ activity }) => {

    const remove = (id) => {
        const newData = this.state.data.filter((performed) => performed.id !== id);
        id(newData);
        this.setState({ data: newData });
    };

    return (
        <table className='table'>
            <thead>
                <tr>
                    <th scope="col">Activity</th>
                    <th scope="col">Date</th>
                    <th scope="col">Start Time</th>
                    <th scope="col">End Time</th>
                    <th scope="col">Duration</th>
                </tr>
            </thead>
            <tbody>
                {activity.map((performed) => (
                    <tr key={performed.id}>
                        <td>{performed.activityName}</td>
                        <td>{performed.activityDate}</td>
                        <td>{performed.startTime}</td>
                        <td>{performed.endTime}</td>
                        <td>{performed.activityDistance}</td>
                        <td>
                            <button onClick={() => remove(performed)}>Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>

    )
}







export default ActivityList;
